function StatsCard({ title, value, icon, color, trend, trendText, progress }) {
  return (
    <div className={`bg-gradient-to-br from-[#0d1117] to-[#161b22] border border-[#30363d] rounded-xl p-5 hover:border-${color} transition-all hover:shadow-lg hover:shadow-${color}/10 group`}>
      <div className="flex items-center justify-between mb-4">
        <div className={`w-12 h-12 bg-${color} bg-opacity-20 rounded-lg flex items-center justify-center group-hover:scale-110 transition-transform`}>
          {icon}
        </div>
        {trend && (
          <div className={`px-2 py-1 bg-${color} bg-opacity-10 rounded-full`}>
            <span className={`text-${color} text-xs font-medium`}>{trend}</span>
          </div>
        )}
      </div>
      <p className="text-[#8b949e] text-sm font-medium mb-1">{title}</p>
      <p className="text-white text-3xl font-bold mb-2">{value}</p>
      {progress && (
        <div className="w-full bg-[#30363d] rounded-full h-1.5 mb-2">
          <div className={`bg-gradient-to-r from-${color} to-${color} h-1.5 rounded-full`} style={{ width: `${progress}%` }}></div>
        </div>
      )}
      {trendText && (
        <p className="text-xs text-[#8b949e]">{trendText}</p>
      )}
    </div>
  );
}

export default StatsCard;
